          type: 'GET',
          url: url,
          data: {
            farm_id: farmId,
            year: isPerDay ? date.getFullYear() : month.getFullYear(),
            month: isPerDay ? date.getMonth() + 1 : month.getMonth() + 1,
            day: isPerDay ? date.getDate() : null
          },
